    private async Task HandleFile(InputFileChangeEventArgs e)
    {
        string[] AllowedExtensions = new string[] { "png", "jpg", "jpeg" };
        var file = e.File;
        var uzanti = AllowedExtensions.Contains(file.Name.Split(".")[1]);
